mapper.insert:
insert into course (courseID, courseTitle, courseContent
  )
values (#{courseid,jdbcType=INTEGER}, #{coursetitle,jdbcType=VARCHAR}, #{coursecontent,jdbcType=VARCHAR}
  )

mapper.update:
